import React, { createContext, useState, useEffect } from 'react';
import axios from 'axios';
import Notification from '../components/Notification';

// Create context
const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);
  const [notification, setNotification] = useState({ message: null, type: null });
  const [error, setError] = useState(null);

  // Check if user is logged in on initial load
  useEffect(() => {
    const checkLoggedIn = async () => {
      // Check if token exists in local storage
      const token = localStorage.getItem('token');

      if (token) {
        // Load user data - Version optimisée pour la production
        const loadUser = async () => {
          if (localStorage.token) {
            setAuthToken(localStorage.token);
          } else {
            setLoading(false);
            return; // Pas de token, pas besoin de charger l'utilisateur
          }

          try {
            // Utiliser le chemin complet pour l'API en production
            const endpoint = process.env.NODE_ENV === 'production' 
              ? '/api/users/profile/' 
              : '/users/profile/';

            const res = await axios.get(endpoint);
            setUser(res.data);
            setIsAuthenticated(true);
            setLoading(false);
          } catch (err) {
            // Supprimer le token invalide
            localStorage.removeItem('token'); 
            setAuthToken(null);
            setUser(null);
            setIsAuthenticated(false);
            setLoading(false);

            // Afficher une notification d'erreur
            setNotification({
              message: 'Session expirée. Veuillez vous reconnecter.',
              type: 'error'
            });
          }
        };

        loadUser();
      } else {
        setLoading(false);
      }
    };

    checkLoggedIn();
  }, []);

  // Set auth token for axios
  const setAuthToken = (token) => {
    if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
    } else {
      delete axios.defaults.headers.common['Authorization'];
    }
  };

  // Register user - Version optimisée pour la production
  const register = async (userData) => {
    try {
      setError(null);
      setNotification({ message: null, type: null });
      setLoading(true);

      // Utiliser le chemin complet pour l'API en production
      const endpoint = process.env.NODE_ENV === 'production' 
        ? '/api/users/register/' 
        : '/users/register/';

      const res = await axios.post(endpoint, {
        username: userData.username,
        email: userData.email,
        password: userData.password,
        password2: userData.password2,
        bio: userData.bio || ''
      });

      // Afficher une notification de succès
      setNotification({
        message: 'Inscription réussie! Vous êtes redirigé vers la page de connexion.',
        type: 'success'
      });

      setLoading(false);

      // Redirection vers la page de connexion
      setTimeout(() => {
        window.location.href = '/login';
      }, 1500); // Délai pour que l'utilisateur puisse voir la notification

      return res.data;
    } catch (err) {
      let message = 'L\'inscription a échoué. Vérifiez votre connexion internet.';

      if (err.response) {
        if (typeof err.response.data === 'object') {
          // Simplifier le message d'erreur
          message = Object.keys(err.response.data)
            .map(key => {
              const value = err.response.data[key];
              return `${key}: ${Array.isArray(value) ? value.join(', ') : value}`;
            })
            .join(', ');
        } else if (typeof err.response.data === 'string') {
          message = err.response.data;
        }
      } else if (err.request) {
        // La requête a été faite mais pas de réponse
        message = 'Erreur réseau: Impossible de contacter le serveur';
      } else {
        // Erreur lors de la configuration de la requête
        message = err.message || 'Erreur inconnue';
      }

      setError(message);
      setNotification({
        message: message,
        type: 'error'
      });
    }
  };

  // Login user - Version simplifiée
  const login = async (userData) => {
    try {
      setError(null);
      setNotification({ message: null, type: null });
      setLoading(true); // Indiquer que le chargement est en cours

      // Utiliser le chemin correct pour l'API
      const endpoint = '/token/';

      // Essayer la connexion avec email
      try {
        const res = await axios.post(endpoint, {
          email: userData.email,
          password: userData.password
        });

        // Sauvegarder le token et configurer l'authentification
        localStorage.setItem('token', res.data.access);
        setAuthToken(res.data.access);

        // Charger les données utilisateur
        const profileEndpoint = '/users/profile/';
        const userRes = await axios.get(profileEndpoint);

        // Mettre à jour l'état
        setUser(userRes.data);
        setIsAuthenticated(true);

        // Afficher une notification de succès
        setNotification({
          message: 'Connexion réussie!',
          type: 'success'
        });

        // Terminer le chargement
        setLoading(false);

        return res.data;
      } catch (emailError) {
        // Si la connexion avec email échoue, essayer avec le nom d'utilisateur
        try {
          // Essayer avec le nom d'utilisateur
          const res = await axios.post(endpoint, {
            username: userData.email, // Utiliser l'email comme nom d'utilisateur
            password: userData.password
          });

          // Sauvegarder le token et configurer l'authentification
          localStorage.setItem('token', res.data.access);
          setAuthToken(res.data.access);

          // Charger les données utilisateur
          const profileEndpoint = '/users/profile/';
          const userRes = await axios.get(profileEndpoint);

          // Mettre à jour l'état
          setUser(userRes.data);
          setIsAuthenticated(true);

          // Afficher une notification de succès
          setNotification({
            message: 'Connexion réussie!',
            type: 'success'
          });

          // Terminer le chargement
          setLoading(false);

          return res.data;
        } catch (usernameError) {
          // Si les deux méthodes échouent, lancer l'erreur
          throw emailError; // Utiliser l'erreur originale
        }
      }
    } catch (err) {
      let message = 'Échec de connexion. Vérifiez vos identifiants.';

      if (err.response) {
        if (typeof err.response.data === 'object') {
          if (err.response.data.detail) {
            message = err.response.data.detail;
          } else {
            // Simplifier le message d'erreur
            message = Object.keys(err.response.data)
              .map(key => {
                const value = err.response.data[key];
                return `${key}: ${Array.isArray(value) ? value.join(', ') : value}`;
              })
              .join(', ');
          }
        } else if (typeof err.response.data === 'string') {
          message = err.response.data;
        }
      } else if (err.request) {
        // La requête a été faite mais pas de réponse
        message = 'Erreur réseau: Impossible de contacter le serveur';
      } else {
        // Erreur lors de la configuration de la requête
        message = err.message || 'Erreur inconnue';
      }

      setError(message);
      setNotification({
        message: message,
        type: 'error'
      });

      // Réinitialiser l'état de chargement en cas d'erreur
      setLoading(false);
    }
  };

  // Logout user
  const logout = () => {
    // Remove token from local storage
    localStorage.removeItem('token');

    // Remove auth token from headers
    setAuthToken(null);

    // Reset state
    setUser(null);
    setIsAuthenticated(false);
  };

  // Update user profile
  const updateProfile = async (userData) => {
    try {
      // Utiliser le chemin correct pour l'API
      const endpoint = process.env.NODE_ENV === 'production' 
        ? '/api/users/me/' 
        : '/users/me/';

      const res = await axios.put(endpoint, userData);
      setUser(res.data);
      return res.data;
    } catch (err) {
      setError(err.response?.data?.detail || 'Profile update failed');
      throw err;
    }
  };

  // Clear errors and notifications
  const clearNotifications = () => {
    setError(null);
    setNotification({ message: null, type: null });
  };

  return (
    <AuthContext.Provider
      value={{
        user,
        isAuthenticated,
        loading,
        error,
        notification,
        register,
        login,
        logout,
        updateProfile,
        clearNotifications
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};

// Export context for use in components
AuthContext.displayName = 'AuthContext';
AuthProvider.context = AuthContext;
export default AuthContext;
