import React, { createContext, useState, useEffect } from 'react';
import axios from 'axios';
import Notification from '../components/Notification';

// Create context
const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);
  const [notification, setNotification] = useState({ message: null, type: null });
  const [error, setError] = useState(null);

  // Check if user is logged in on initial load
  useEffect(() => {
    const checkLoggedIn = async () => {
      // Check if token exists in local storage
      const token = localStorage.getItem('token');

      if (token) {
        // Load user data - Version optimisée pour la production
        const loadUser = async () => {
          if (localStorage.token) {
            setAuthToken(localStorage.token);
          } else {
            setLoading(false);
            return; // Pas de token, pas besoin de charger l'utilisateur
          }

          try {
            // Utiliser le chemin complet pour l'API en production
            const endpoint = process.env.NODE_ENV === 'production' 
              ? '/api/users/profile/' 
              : '/users/profile/';

            const res = await axios.get(endpoint);
            setUser(res.data);
            setIsAuthenticated(true);
            setLoading(false);
          } catch (err) {
            console.error('Erreur de chargement utilisateur:', err);
            localStorage.removeItem('token'); // Supprimer le token invalide
            setAuthToken(null);
            setUser(null);
            setIsAuthenticated(false);
            setLoading(false);
          }
        };

        loadUser();
      } else {
        setLoading(false);
      }
    };

    checkLoggedIn();
  }, []);

  // Set auth token for axios
  const setAuthToken = (token) => {
    if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
    } else {
      delete axios.defaults.headers.common['Authorization'];
    }
  };

  // Register user - Version simplifiée
  const register = async (userData) => {
    try {
      console.log('Tentative d\'inscription avec:', userData);

      // Endpoint d'inscription simplifié
      const endpoint = '/api/users/register/';

      const res = await axios.post(endpoint, {
        username: userData.username,
        email: userData.email,
        password: userData.password,
        password2: userData.password2,
        bio: userData.bio || ''
      });

      console.log('Réponse d\'inscription:', res.data);

      // Afficher une alerte simple de succès
      alert('Inscription réussie! Vos données ont été enregistrées dans la base de données. Vous pouvez maintenant vous connecter.');

      // Rediriger vers la page de connexion immédiatement
      window.location.href = '/login';

      return res.data;
    } catch (err) {
      console.error('Erreur d\'inscription:', err);

      // Message d'erreur simplifié
      let message = 'L\'inscription a échoué. Vérifiez votre connexion internet.';

      if (err.response?.data) {
        console.log('Données d\'erreur:', err.response.data);
        if (typeof err.response.data === 'object') {
          // Simplifier le message d'erreur
          const errorMessages = [];
          for (const key in err.response.data) {
            const value = err.response.data[key];
            errorMessages.push(`${key}: ${Array.isArray(value) ? value.join(', ') : value}`);
          }
          message = errorMessages.join('\n');
        } else if (typeof err.response.data === 'string') {
          message = err.response.data;
        }
      } else if (err.request) {
        // La requête a été faite mais pas de réponse
        message = 'Erreur réseau: Impossible de contacter le serveur';
      } else {
        // Erreur lors de la configuration de la requête
        message = err.message || 'Erreur inconnue';
      }

      setError(message);
      setNotification({
        message: message,
        type: 'error'
      });
    }
  };

  // Login user - Version simplifiée
  const login = async (userData) => {
    try {
      console.log('Tentative de connexion avec:', userData);

      // Endpoint de connexion simplifié
      // Éviter le doublon api/api en utilisant le chemin correct
      const endpoint = '/token/';

      console.log('URL de connexion:', axios.defaults.baseURL + endpoint);

      // Le backend attend 'email' comme champ d'authentification
      const res = await axios.post(endpoint, {
        email: userData.email || userData.username, // Accepte soit email soit username
        password: userData.password
      });

      console.log('Réponse de connexion:', res.data);

      // Sauvegarder le token dans le stockage local
      localStorage.setItem('token', res.data.access);

      // Configurer le token d'authentification pour les futures requêtes
      setAuthToken(res.data.access);

      // Charger les données de l'utilisateur
      const profileEndpoint = '/api/users/profile/';

      const userRes = await axios.get(profileEndpoint);

      // Mettre à jour l'état avec les données de l'utilisateur
      setUser(userRes.data);
      setIsAuthenticated(true);

      // Afficher une alerte simple de succès
      alert('Connexion réussie! Vos données ont été chargées depuis la base de données.');

      // Rediriger vers la page d'accueil immédiatement
      window.location.href = '/';

      return res.data;
    } catch (err) {
      console.error('Erreur de connexion:', err);

      // Message d'erreur simplifié
      let message = 'Échec de connexion. Vérifiez vos identifiants.';

      if (err.response?.data) {
        console.log('Données d\'erreur:', err.response.data);
        if (typeof err.response.data === 'object') {
          if (err.response.data.detail) {
            message = err.response.data.detail;
          } else {
            // Simplifier le message d'erreur
            const errorMessages = [];
            for (const key in err.response.data) {
              const value = err.response.data[key];
              errorMessages.push(`${key}: ${Array.isArray(value) ? value.join(', ') : value}`);
            }
            message = errorMessages.join('\n');
          }
        } else if (typeof err.response.data === 'string') {
          message = err.response.data;
        }
      } else if (err.request) {
        // La requête a été faite mais pas de réponse
        message = 'Erreur réseau: Impossible de contacter le serveur';
      } else {
        // Erreur lors de la configuration de la requête
        message = err.message || 'Erreur inconnue';
      }

      // Afficher l'erreur avec une alerte simple
      alert('Erreur: ' + message);
    }
  };

  // Logout user
  const logout = () => {
    // Remove token from local storage
    localStorage.removeItem('token');

    // Remove auth token from headers
    setAuthToken(null);

    // Reset state
    setUser(null);
    setIsAuthenticated(false);
  };

  // Update user profile - Version optimisée pour la production
  const updateProfile = async (userData) => {
    try {
      setError(null);
      setNotification({ message: null, type: null });

      // Utiliser le chemin complet pour l'API en production
      // Le chemin correct est '/api/users/me/' en production et '/api/users/me/' en développement
      // car nous avons déjà configuré axios.defaults.baseURL dans App.js
      const endpoint = '/api/users/me/';

      const res = await axios.put(endpoint, userData);
      setUser(res.data);

      // Afficher une notification de succès
      setNotification({
        message: 'Profil mis à jour avec succès!',
        type: 'success'
      });

      return res.data;
    } catch (err) {
      console.error('Erreur de mise à jour du profil:', err);

      let message = 'La mise à jour du profil a échoué';

      if (err.response?.data) {
        if (typeof err.response.data === 'object') {
          if (err.response.data.detail) {
            message = err.response.data.detail;
          } else {
            // Simplifier le message d'erreur
            message = Object.keys(err.response.data)
              .map(key => {
                const value = err.response.data[key];
                return `${key}: ${Array.isArray(value) ? value.join(', ') : value}`;
              })
              .join(', ');
          }
        } else if (typeof err.response.data === 'string') {
          message = err.response.data;
        }
      } else if (err.request) {
        // La requête a été faite mais pas de réponse
        message = 'Erreur réseau: Impossible de contacter le serveur';
      } else {
        // Erreur lors de la configuration de la requête
        message = err.message || 'Erreur inconnue';
      }

      setError(message);
      setNotification({
        message: message,
        type: 'error'
      });

      throw err;
    }
  };

  // Clear errors and notifications
  const clearNotifications = () => {
    setError(null);
    setNotification({ message: null, type: null });
  };

  return (
    <AuthContext.Provider
      value={{
        user,
        isAuthenticated,
        loading,
        error,
        notification,
        register,
        login,
        logout,
        updateProfile,
        clearNotifications
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};

// Export context for use in components
AuthContext.displayName = 'AuthContext';
AuthProvider.context = AuthContext;
export default AuthContext;
